import React from 'react';
import { Layout, Menu, Input, Typography, Space, ConfigProvider } from 'antd';
import {
  DashboardOutlined,
  BarChartOutlined,
  ThunderboltOutlined,
  ShoppingOutlined,
  SearchOutlined,
} from '@ant-design/icons';

const { Header } = Layout;
const { Text } = Typography;

// --- SVG abstract bird logo ---
const BirdLogo = () => (
  <svg width="48" height="48" viewBox="0 0 48 48" style={{ display: 'block' }}>
    <defs>
      <linearGradient id="birdBody" x1="0%" y1="0%" x2="100%" y2="100%">
        <stop offset="0%" stopColor="#7B3FE4" /> {/* 紫色 */}
        <stop offset="100%" stopColor="#FFA200" /> {/* 橙色 */}
      </linearGradient>
      <linearGradient id="birdWing" x1="0%" y1="0%" x2="100%" y2="100%">
        <stop offset="0%" stopColor="#FFA200" />
        <stop offset="100%" stopColor="#7B3FE4" />
      </linearGradient>
    </defs>
    <g>
      {/* Abstract Bird Body */}
      <path
        d="M10 38 Q24 8 38 23 Q43 28 32 36 Q26 39 19 37 Z"
        fill="url(#birdBody)"
      />
      {/* Wing */}
      <path
        d="M22 31 Q29 21 36 28 Q32 33 25 32 Z"
        fill="url(#birdWing)"
        opacity="0.85"
      />
      {/* Bird Head */}
      <circle cx="15" cy="25" r="5" fill="#7B3FE4" />
      {/* Bird Beak */}
      <polygon points="12,25 6,23 14,27" fill="#FFA200" />
    </g>
  </svg>
);

// --- Logo Brand Component ---
const BrandLogo = () => (
  <Space align="center" style={{ height: 64 }}>
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <BirdLogo />
      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          marginLeft: 14,
          justifyContent: 'center',
        }}
      >
        <Text style={{ fontSize: 24, color: '#22293B', fontWeight: 700, lineHeight: 1 }}>
          Bresendy Agency
        </Text>
        <Text style={{ fontSize: 15, color: '#9599A8', lineHeight: 1.3 }}>
          Marketing team
        </Text>
      </div>
    </div>
  </Space>
);

// --- Navigation Menu Items ---
const menuItems = [
  {
    key: 'dashboard',
    icon: <DashboardOutlined />,
    label: 'Dashboard',
    status: 'active',
  },
  {
    key: 'report',
    icon: <BarChartOutlined />,
    label: 'Report',
    status: 'inactive',
  },
  {
    key: 'campaign',
    icon: <ThunderboltOutlined />,
    label: 'Campaign',
    status: 'inactive',
  },
  {
    key: 'products',
    icon: <ShoppingOutlined />,
    label: 'Products',
    status: 'inactive',
  },
];

// --- Navigation Menu Component ---
const NavigationMenu = () => (
  <Menu
    mode="horizontal"
    selectedKeys={['dashboard']}
    style={{
      background: 'transparent',
      border: 'none',
      display: 'flex',
      alignItems: 'center',
      fontWeight: 600,
      fontSize: 16,
      height: 64,
      flex: 1,
      justifyContent: 'center',
      marginLeft: 48,
      marginRight: 48,
    }}
    overflowedIndicator={null}
  >
    {menuItems.map(({ key, icon, label, status }) => (
      <Menu.Item
        key={key}
        style={{
          backgroundColor: status === 'active' ? '#F4F6F9' : 'transparent',
          color: status === 'active' ? '#22293B' : '#9599A8',
          display: 'flex',
          alignItems: 'center',
          borderRadius: 10,
          marginRight: 6,
          fontWeight: status === 'active' ? 700 : 500,
          marginTop: 0,
          marginBottom: 0,
        }}
        icon={
          React.cloneElement(icon, {
            style: {
              fontSize: 20,
              color: status === 'active' ? '#22293B' : '#B6BAC7',
              marginRight: 10,
            },
          })
        }
      >
        <span
          style={{
            color: status === 'active' ? '#22293B' : '#B6BAC7',
            verticalAlign: 'middle',
            fontSize: 17,
          }}
        >
          {label}
        </span>
      </Menu.Item>
    ))}
  </Menu>
);

// --- Global Search Box ---
const GlobalSearch = () => (
  <Input
    prefix={
      <SearchOutlined style={{ color: '#B6BAC7', fontSize: 18, marginRight: 7 }} />
    }
    placeholder="Search information"
    size="large"
    style={{
      background: 'rgba(255,255,255,0.82)',
      border: 'none',
      borderRadius: 24,
      width: 270,
      fontSize: 16,
      boxShadow: '0 1px 4px rgba(80,96,144,0.04)',
      color: '#B6BAC7',
    }}
    allowClear={false}
    bordered={false}
    className="global-search-custom"
  />
);

// --- App Layout ---
const App = () => (
  <ConfigProvider theme={{ token: { fontFamily: 'Inter, Roboto, sans-serif' } }}>
    <Layout
      style={{
        minHeight: '100vh',
        minWidth: '100vw',
        backgroundColor: '#F6F7FB',
        width: 1920,
        height: 1080,
        overflow: 'hidden',
      }}
    >
      {/* Header Bar */}
      <Header
        style={{
          padding: '0 48px',
          background: '#fff',
          height: 90,
          boxShadow: '0 4px 24px 0px rgba(44, 40, 83, 0.07)',
          display: 'flex',
          alignItems: 'center',
        }}
      >
        <div
          style={{
            height: '100%',
            width: '100%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'flex-start',
          }}
        >
          <div style={{ minWidth: 330, display: 'flex', alignItems: 'center' }}>
            <BrandLogo />
          </div>
          <div style={{ flex: 1, minWidth: 680, maxWidth: 880, height: '100%' }}>
            <NavigationMenu />
          </div>
          <div style={{ display: 'flex', alignItems: 'center', minWidth: 330, justifyContent: 'flex-end' }}>
            <GlobalSearch />
          </div>
        </div>
      </Header>

      {/* Filler main content for full-page display */}
      <Layout.Content
        style={{
          flex: 1,
          minHeight: 0,
          background: '#F6F7FB',
        }}
      >
        {/* Decorative Illustration / Section Fillers */}
        <div
          style={{
            position: 'absolute',
            left: 0,
            right: 0,
            top: 100,
            bottom: 0,
            zIndex: 0,
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'space-between',
            alignItems: 'stretch',
            pointerEvents: 'none',
          }}
        >
          <svg width="350" height="320" style={{ marginLeft: 32, marginTop: 12, opacity: 0.16 }}>
            <ellipse
              cx="170"
              cy="150"
              rx="140"
              ry="90"
              fill="#A28CFF"
              opacity="0.20"
            />
          </svg>
          <svg width="260" height="260" style={{ marginRight: 98, marginTop: 70, opacity: 0.10 }}>
            <ellipse
              cx="130"
              cy="130"
              rx="120"
              ry="70"
              fill="#FFE6B2"
              opacity="0.28"
            />
          </svg>
        </div>
        <div
          style={{
            position: 'relative',
            zIndex: 1,
            width: '100%',
            height: 'calc(100vh - 90px)',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            pointerEvents: 'none',
          }}
        >
          <Text style={{ color: '#D3D7E4', fontSize: 54, fontWeight: 700, opacity: 0.45 }}>
            Welcome to Bresendy Agency Dashboard
          </Text>
          <Text style={{ color: '#B6BAC7', fontSize: 22, marginTop: 18, opacity: 0.80 }}>
            All your marketing campaigns in one place.
          </Text>
        </div>
      </Layout.Content>
    </Layout>
    {/* Minimal global search input placeholder color override */}
    <style>
      {`
        .global-search-custom input::placeholder {
          color: #B6BAC7 !important;
          opacity: 1 !important;
          font-size: 16px !important;
        }
      `}
    </style>
  </ConfigProvider>
);

export default App;
