import React, { useState } from 'react';
import { Layout, Tabs, Typography, Button, Image, Input, List } from 'antd';
import { CloseOutlined } from '@ant-design/icons';

const { Header, Content } = Layout;
const { Title, Paragraph } = Typography;
const { TextArea } = Input;

const App = () => {
  const [activeTab, setActiveTab] = useState('about');

  // Mock data
  const aboutData = {
    version: 'qBittorrent v4.4.5',
    buildInfo: 'Built on June 15, 2023',
    developer: 'Developed by Christophe Dumez and contributors'
  };

  const linksData = [
    { name: 'Homepage', url: 'https://www.qbittorrent.org' },
    { name: 'GitHub', url: 'https://github.com/qbittorrent/qBittorrent' },
    { name: 'Forum', url: 'https://forum.qbittorrent.org' },
    { name: 'Documentation', url: 'https://wiki.qbittorrent.org' }
  ];

  const licenseText = `qBittorrent is licensed under the GNU General Public License version 2 (GPLv2).

This program is free software; you can redistribute it and/or modify it under the terms of the GNU General Public License as published by the Free Software Foundation; either version 2 of the License, or (at your option) any later version.

This program is distributed in the hope that it will be useful, but WITHOUT ANY WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE. See the GNU General Public License for more details.

You should have received a copy of the GNU General Public License along with this program; if not, write to the Free Software Foundation, Inc., 51 Franklin Street, Fifth Floor, Boston, MA 02110-1301, USA.`;

  const contributorsData = [
    'Christophe Dumez - Lead Developer',
    'Sledgehammer999 - Core Developer',
    'Chocobo1 - Core Developer',
    'FranciscoPombal - Core Developer',
    'Mike Tzou - Core Developer',
    'Various translators and community members'
  ];

  // Generate unique IDs
  const generateId = (prefix) => `${prefix}_${Math.random().toString(36).substr(2, 9)}`;

  const modalHeaderId = generateId('Modal_Header');
  const modalTabNavigationId = generateId('Modal_Tab_Navigation');
  const modalContentAreaId = generateId('Modal_Content_Area');

  return (
    <Layout 
      style={{ 
        width: '100%', 
        height: '100vh', 
        backgroundColor: '#2B2B2B',
        fontFamily: 'Arial, sans-serif'
      }}
      data-spec="App_Layout"
    >
      {/* Modal Header */}
      <Header 
        style={{ 
          backgroundColor: '#2B2B2B', 
          padding: '16px', 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          borderBottom: '1px solid #3C3C3C'
        }}
        data-spec={modalHeaderId}
      >
        <Title 
          level={3} 
          style={{ 
            color: '#CCCCCC', 
            margin: 0,
            fontSize: '18px'
          }}
          data-spec="Modal_Title"
        >
          About qBittorrent
        </Title>
        <Button
          type="text"
          icon={<CloseOutlined style={{ color: '#CCCCCC' }} />}
          style={{
            width: '32px',
            height: '32px',
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
          data-spec="Close_Button"
        />
      </Header>

      {/* Tab Navigation */}
      <div 
        style={{ 
          backgroundColor: '#2B2B2B', 
          borderBottom: '1px solid #3C3C3C'
        }}
        data-spec={modalTabNavigationId}
      >
        <Tabs
          activeKey={activeTab}
          onChange={setActiveTab}
          tabBarStyle={{
            margin: 0,
            paddingLeft: '16px',
            color: '#CCCCCC'
          }}
          items={[
            {
              key: 'about',
              label: 'About',
              style: { color: activeTab === 'about' ? '#FFFFFF' : '#888888' }
            },
            {
              key: 'license',
              label: 'License',
              style: { color: activeTab === 'license' ? '#FFFFFF' : '#888888' }
            },
            {
              key: 'contributors',
              label: 'Contributors',
              style: { color: activeTab === 'contributors' ? '#FFFFFF' : '#888888' }
            }
          ]}
          tabBarGutter={32}
          inkBarStyle={{ backgroundColor: '#BDD163' }}
          data-spec="Tab_Navigation"
        />
      </div>

      {/* Content Area */}
      <Content 
        style={{ 
          padding: '24px',
          backgroundColor: '#2B2B2B',
          display: 'flex',
          height: 'calc(100vh - 120px)'
        }}
        data-spec={modalContentAreaId}
      >
        {/* Left Column - Illustration */}
        <div 
          style={{ 
            width: '30%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            paddingRight: '24px'
          }}
          data-spec="Illustration_Column"
        >
          <div style={{ width: '100%', textAlign: 'center' }} data-spec="Illustration_Container">
            <svg 
              width="180" 
              height="180" 
              viewBox="0 0 256 256" 
              style={{ maxWidth: '100%', height: 'auto' }}
              data-spec="App_Logo"
            >
              <rect width="256" height="256" rx="20" fill="#3C3C3C" />
              <circle cx="128" cy="128" r="80" fill="#BDD163" />
              <path d="M88 128L118 158L168 108" stroke="#2B2B2B" strokeWidth="16" fill="none" />
            </svg>
          </div>
        </div>

        {/* Right Column - Content */}
        <div 
          style={{ 
            width: '70%',
            display: 'flex',
            flexDirection: 'column',
            paddingLeft: '24px',
            borderLeft: '1px solid #3C3C3C'
          }}
          data-spec="Content_Column"
        >
          {activeTab === 'about' && (
            <div data-spec="About_Content">
              <Paragraph style={{ color: '#CCCCCC', marginBottom: '16px' }} data-spec="Version_Info">
                {aboutData.version}
              </Paragraph>
              <Paragraph style={{ color: '#CCCCCC', marginBottom: '16px' }} data-spec="Build_Info">
                {aboutData.buildInfo}
              </Paragraph>
              <Paragraph style={{ color: '#CCCCCC', marginBottom: '24px' }} data-spec="Developer_Info">
                {aboutData.developer}
              </Paragraph>

              <div style={{ marginBottom: '24px' }} data-spec="Links_Section">
                {linksData.map((link, index) => (
                  <div key={index} style={{ marginBottom: '8px' }} data-spec={`Link_Item_${index}`}>
                    <a 
                      href={link.url} 
                      style={{ color: '#BDD163', textDecoration: 'none' }}
                      data-spec={`Link_${index}`}
                    >
                      {link.name}
                    </a>
                  </div>
                ))}
              </div>

              <Button 
                type="primary" 
                style={{ 
                  backgroundColor: '#BDD163', 
                  color: '#2B2B2B', 
                  borderColor: '#BDD163',
                  borderRadius: '4px',
                  alignSelf: 'flex-start'
                }}
                data-spec="Update_Button"
              >
                Check for Updates
              </Button>
            </div>
          )}

          {activeTab === 'license' && (
            <div data-spec="License_Content">
              <TextArea
                value={licenseText}
                readOnly
                autoSize={{ minRows: 15 }}
                style={{
                  backgroundColor: '#3C3C3C',
                  color: '#CCCCCC',
                  borderColor: '#555555',
                  borderRadius: '4px',
                  minHeight: '300px'
                }}
                data-spec="License_TextArea"
              />
            </div>
          )}

          {activeTab === 'contributors' && (
            <div data-spec="Contributors_Content">
              <List
                dataSource={contributorsData}
                renderItem={(item, index) => (
                  <List.Item 
                    style={{ 
                      backgroundColor: 'transparent', 
                      borderBottom: '1px solid #3C3C3C',
                      padding: '12px 0'
                    }}
                    data-spec={`Contributor_Item_${index}`}
                  >
                    <Typography.Text style={{ color: '#CCCCCC' }} data-spec={`Contributor_Text_${index}`}>
                      {item}
                    </Typography.Text>
                  </List.Item>
                )}
                style={{ 
                  backgroundColor: 'transparent',
                  maxHeight: '400px',
                  overflowY: 'auto'
                }}
                data-spec="Contributors_List"
              />
            </div>
          )}
        </div>
      </Content>
    </Layout>
  );
};

export default App;
